import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons
import Chat from "@material-ui/icons/Chat";
import VerifiedUser from "@material-ui/icons/VerifiedUser";
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import InfoArea from "components/InfoArea/InfoArea.js";

import styles from "assets/jss/material-kit-react/views/landingPageSections/productStyle.js";

const useStyles = makeStyles(styles);
const text = ['Line 1. ', <br />, 'Line 2.'];

export default function DetailsSection() {
  const classes = useStyles();
  return (
    <div className={classes.section}>
      <GridContainer justify="center">
        <GridItem xs={12} sm={12} md={8}>
          <h2 className={classes.title}>Tattershall Park Facilities</h2>
          <h5 className={classes.description}>Things to know</h5>
        </GridItem>
      </GridContainer>
      <div>
        <GridContainer>
          <GridItem xs={12} sm={6}>
            <InfoArea
              title="Free Chat"
              description="Fresh air, lakeside location, relaxing park facilities – all on your doorstep far away from the hustle and bustle of everyday life. Forget about deciding where to go and the hassle of having to book up way in advance – simply step inside and it’s your very own home from home every time.
              Of course, no holiday park is truly complete without a wide range of facilities that provide you with options every visit including; Heated indoor pool with gym & hot tub, beach and outdoor entertainment area, great food and drink, adventure golf, outdoor adventure playgrounds, aqua park, water-ski lake and more.

              We’re located in Lincolnshire, one of the largest counties in Britain meaning there is something for everyone. This historic county caters for individuals, couples and families, whatever your age you will have a ball. There are festivals, stately homes, castles – in fact one of the best is just a few hundred yards from our park. There is also mile upon mile of great cycling routes, beautiful coast & country walks and some of the best-known seaside towns in the UK. With a superb selection of holiday homes available to view as well as a stunning new pitch development, Tattershall Lakes really is the perfect location to own your very own holiday home."
              icon={Chat}
              iconColor="info"
              vertical
            />
          </GridItem>
          <GridItem xs={12} sm={6}>
            <InfoArea
              title="Verified Users"
              description="The sumptuous living area is
              divine, with a plump and lengthy plush sofa that’s perfectly
              positioned next to the gorgeous full-length windows, creating the
              perfect spot for a summer laze. A beautiful feature fireplace is the
              icing on the cake for those cooler evenings in, creating a welcoming
              and snug environment to relax or entertain."
              icon={VerifiedUser}
              iconColor="success"
              vertical
            />
          </GridItem>
        </GridContainer>
      </div>
    </div>
  );
}
